import React from 'react';
import Type from '../Type/Type';
import './Card.scss'


const Card = ({
  image,
  imageName,
  minHeight = '357px',
  maxHeight = '550px',
  title,
  generation,
}) => {
  return (
    <div className='card'>
      <img className='card__image' src={image} alt={imageName} style={{ minHeight, maxHeight }} />
      <h4 className='card__title'>{title}</h4>
      <span className='card__generation'>Generation: {generation}</span>
      

      <div className='type'>
        <span className='card__type'>Type: </span>
        <Type/>
      </div>  
      <button className='card__button'>Choose</button>
    </div>  
     
    
  );
};

export default Card;
